            + " + "
            + pl.col("max_bootstrapped_demos").cast(pl.Utf8)
        ),
        chain_of_thought=pl.when(pl.col("chain_of_thought"))
        .then(pl.lit("‚úÖ"))
        .otherwise(pl.lit("‚ùå")),
    )
    .sort("f1", descending=True)
    .select(
        "model",
        "method",
